mapper.select:
SELECT cb.com_b_cnt, cb.com_b_date, cb.com_b_no, cb.com_b_type, cb.com_photo, cb.com_title, cb.com_b_view, cb.mem_no, m.mem_name, m.mem_id FROM COMMON_BOARD cb, (select mem_name, mem_no, mem_id from member) m WHERE cb.mem_no = m.mem_no
AND COM_B_TYPE = #{com_b_type}
AND com_b_no = #{com_b_no}
ORDER BY cb.com_b_no DESC

mapper.insert:
 INSERT INTO ANYMERCY.COMMON_BOARD (
	   COM_B_CNT, COM_B_DATE, COM_B_NO, 
	   COM_B_TYPE, COM_PHOTO, COM_TITLE, 
	   COM_B_VIEW, MEM_NO) 
		VALUES ( 
		 #{com_b_cnt}
		,to_char(sysdate,'yyyy-mm-dd')
		,seq_com_b_no.nextval
		,#{com_b_type}
		,#{com_photo}
		,#{com_title}
		,0
		,#{mem_no}
 )

  	INSERT INTO ANYMERCY.DONATION (
   DON_AMOUNT, DON_DATE, DON_NO, 
   DON_NOTI_NO, MEM_NO) 
VALUES ( 
				#{don_noti_amount}
				,to_char(sysdate,'yyyy/mm/dd')
				,seq_don_no.nextval
				,#{don_noti_no}
				,#{mem_no}
				)
